set NAMES UTF8;
DROP DATABASE IF EXISTS  book;
CREATE DATABASE book CHARSET=UTF8;
USE book;

CREATE TABLE book_women(
    wid INT  PRIMARY  KEY  AUTO_INCREMENT,
    btitle VARCHAR(32) UNIQUE ,
    bpic_s VARCHAR(50),
    bpic_b VARCHAR(50),
    wordcount int,
    introduce VARCHAR (100),
    click int,
    author VARCHAR(16),
    down_url VARCHAR(55)
);

CREATE TABLE book_man(
    mid INT  PRIMARY  KEY  AUTO_INCREMENT,
    btitle VARCHAR(32) UNIQUE ,
    bpic_s VARCHAR(50),
    bpic_b VARCHAR(50),
    wordcount int,
    introduce VARCHAR (300),
    click int,
    author VARCHAR(16),
    down_url VARCHAR(55)
);

CREATE TABLE top_10(
    mid INT  PRIMARY  KEY  AUTO_INCREMENT,
    btitle VARCHAR(32) UNIQUE ,
    bpic_s VARCHAR(50),
    bpic_b VARCHAR(50),
    wordcount int,
    introduce VARCHAR (300),
    click int,
    author VARCHAR(16),
    down_url VARCHAR(55)
);







CREATE TABLE  book_user(
    user_id INT  PRIMARY KEY  AUTO_INCREMENT,
    user VARCHAR(16) UNIQUE,
    pwd VARCHAR(16)
);








